package wil.liam.demo.myapplication.demo;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import wil.liam.demo.myapplication.R;import wil.liam.demo.mylibrary.RingtonePlayer;public class DemoActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_demo);        findViewById(R.id.start).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                RingtonePlayer.Instance().play(DemoActivity.this,R.raw.musicbox,true);            }        });        findViewById(R.id.stop).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                RingtonePlayer.Instance().stop();            }        });    }}