package wil.liam.demo.mylibrary;import android.content.ClipData;import android.content.Context;import android.os.Build;import android.text.ClipboardManager;public class ClipboardUtils {    public static boolean copyTo(Context context, String text){        if (StringUtils.isEmpty(text))            return false;        try{            int sdk = Build.VERSION.SDK_INT;            if (sdk < Build.VERSION_CODES.HONEYCOMB){                ClipboardManager clipboard = (ClipboardManager) context.getSystemService(Context.CLIPBOARD_SERVICE);                clipboard.setText(text);            }else {                android.content.ClipboardManager clipboard = (android.content.ClipboardManager) context                        .getSystemService(Context.CLIPBOARD_SERVICE);                ClipData clip = ClipData.newPlainText("Copied Text",text);                clipboard.setPrimaryClip(clip);            }            return true;        }catch (Exception e){            return false;        }    }}