package wil.liam.demo.mylibrary;import android.app.Activity;import android.content.Context;import android.view.View;import android.view.inputmethod.InputMethodManager;public class IMEUtils {    public static void hideIME(Activity activity){        final View view = activity.getCurrentFocus();        hideIME(activity,view);    }    private static void hideIME(Activity activity, View view) {        if (view == null)            return;        final InputMethodManager imm = (InputMethodManager) activity.getSystemService(Context.INPUT_METHOD_SERVICE);        imm.hideSoftInputFromWindow(view.getWindowToken(),0);    }    public static void showIME(Context context, View view){        if (view != null && view.requestFocus()){            InputMethodManager imm = (InputMethodManager) context.getSystemService(Context.INPUT_METHOD_SERVICE);            imm.showSoftInput(view,0);        }    }}